<style scoped>
  .who-are-use-section {
    margin-top: 100px;
    margin-bottom: -60px;
  }

  .who-are-use h2 {
    font-size: 24px;
    text-align: center;
    font-weight: 600;
  }

  .who-are-use .grid {
    display: flex;
    flex-wrap: wrap;
    gap: 6px;
    margin-top: 40px;
  }

  .who-are-use .grid .item {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 120px;
    background-color: var(--vp-c-bg-soft);
    border-radius: 4px;
    transition: background-color .2s;
    cursor: pointer;
    color: inherit;
  }

  .who-are-use .grid .item .image {
    height: 34px;
  }

  .who-are-use .grid .item:hover {
    background-color: var(--vp-c-bg-soft-down);
  }

  .dark .who-are-use .grid .item .image {
    filter: grayscale(1) invert(1);
  }

  .dark .who-are-use .grid .item:hover {
    background-color: var(--vp-c-neutral);
  }

  .dark .who-are-use .grid .item:hover .image {
    filter: grayscale(0) invert(0);
  }

  @media (min-width: 640px) {
    .who-are-use .grid .item {
      width: calc((100% - 12px) / 3);
    }
  }

  @media (min-width: 960px) {
    .who-are-use .grid .item {
      width: calc((100% - 18px) / 4);
    }
  }

  .who-are-use .become-next {
    display: flex;
    flex-direction: row;
    justify-content: center;
    padding-top: 50px;
  }
  .who-are-use .become-next a {
    font-size: 14px;
    line-height: 36px;
    border-radius: 18px;
    padding: 0 20px;
    color: var(--vp-c-text-3);
    font-weight: 400;
    border: solid 1px var(--vp-button-alt-border);
    cursor: pointer;
    transition: border-color 0.2s, color 0.2s;
  }
  .who-are-use .become-next a:hover {
    border-color: var(--vp-c-brand);
    color: var(--vp-c-brand);
  }
</style>